let destinations = [
    {
        rating: 10,
        location: 'Roman Colosseum',
        description: 'Where Gladiators fought to the death, and people watched for entertainment',
        image: "data:image/jpeg;base64,",
        review: "",
        id: 0
    },
    {
        rating: 10,
        location: 'Eiffel Tower',
        description: 'Famous tower in France',
        image: "data:image/jpeg;base64,",
        review: "",
        id: 1
    },
    {
        rating: 10,
        location: 'Leaning Tower of Pisa',
        description: 'Famous tower that is falling over, but still standing',
        image: "data:image/jpeg;base64,",
        review: "",
        id: 2
    },
    {
        rating: 10,
        location: 'Great Wall of China',
        description: 'The longest and most famous wall in the world',
        image: "data:image/jpeg;base64,",
        review: "",
        id: 3
    },
]
let id = 4

module.exports = {
    getDestination: (req, res) => {
        res.status(200).send(destinations)
    },

    addDestination: (req, res) => {
        const {location, description, image, rating, review} = req.body
        const destination = {
            rating: rating,
            location: location,
            description: description,
            image: image,
            review: review,
            id: id
        }
        id++
        destinations.push(destination)
        res.status(200).send(destinations)
    },

    deleteDestination: (req, res) => {
        const {id} = req.params
        const index = destinations.findIndex((e) => {
            return e.id === +id
        })
        destinations.splice(index, 1)
        res.status(200).send(destinations)
    },

    addReview: (req, res) => {
        const {id} = req.params
        const {rating, review} = req.body
        const index = destinations.findIndex((e) => {
            return e.id === +id
        })
        destinations[index] = {
            ...destinations[index],
            rating: rating,
            review: review
        }
        res.status(200).send(destinations)
    }  
}